sys_variable_value.value:
(function execute(inputs, outputs) {

  var items = inputs.object.items;
  var obj = {};

  for(var i = 0; i < items.length; i++){
		var field = items[i].field;
		var oldValue = items[i].fromString;
		var newValue= items[i].toString;

    var mp = new GlideRecord(inputs.mapTable);
    	mp.addQuery('active', 'true');
    	mp.addQuery('external_system', inputs.externalSystem);
    	mp.addQuery('external_column_name', field);
    	mp.query();

    if(mp.next()){

      obj.sn_field = mp.getValue('field');
      obj.sn_fieldvaltype = mp.getValue('value_type');
      obj.value = newValue;
      obj.target_table = mp.getValue('table');
    }
}
  	  outputs.sn_field = obj.sn_field; //ServiceNow Field
      outputs.sn_fieldvaltype = obj.sn_fieldvaltype; //Service Now field set type i.e. Value or Display Value (if reference field)
      outputs.value = obj.value; //Value to set in ServiceNow
  	  outputs.target = obj.target_table; //target table in ServiceNow

})(inputs, outputs);

