sys_variable_value.value:
(function execute(inputs, outputs) {
 outputs.result = 'fail';

  var grRec = new GlideRecord(inputs.targetTable);
  if(grRec.get('correlation_id', inputs.id)){

     grRec.setValue(inputs.updateField, inputs.value);
     grRec.update();

  outputs.result = 'success';
     }

})(inputs, outputs);

